import {useEffect, useState} from 'react'
import Axios from 'axios'

import styles from './Tabela.module.css'
function Tabela({dividas,setDividas}){
    const [lista,setLista] =  useState()
    const [exibir,setExibir] = useState(true);

    useEffect(()=>{
    
        Axios.get('http://localhost:3500/dividas').then((response) =>{
            setLista(response.data)
        })
        
  },[]); 
    

const mostra_esconde =()=>{
    console.log
    setExibir = false;
}

    return(
    
    
    
    
    <> { exibir === true &&
            (<div className={styles.testando} onClick={mostra_esconde}>
                eieie
            </div>)
        }
        {exibir === false && (

            <div>

                        <table >
                        
                                    <thead>
                                        
                                                <tr >
                                                            <th>Despesas</th>
                                                            <th>Data</th>
                                                            <th>Valor</th>                     
                                                </tr>

                                    </thead>


                                    <tbody className={styles.teste}>
                                    
                                                {typeof lista !== 'undefined' && lista.map((list) =>{
                                                        return <tr key={list.id}>
                                                                        <td className={styles.colun_espace}>{list.descricao}</td>
                                                                        <td className={styles.colun_center}>{list.data}</td>
                                                                        <td className={styles.colun_center}>{list.valor}</td>                     
                                                                </tr>
                                                })}
                                
                                    </tbody>
                            

                        </table>
                </div>
        )}
    </>

    )
}

export default Tabela
