import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom'
import Home from './Components/pages/Home'
import Contato from './Components/pages/Contato'
import Sobre from './Components/pages/Sobre'
import Navbar from './Components/layout/NavBar'
import Footer from './Components/layout/Footer'
import Despesas from './Components/pages/Despesas'
import Nova_conta from './Components/pages/Nova_conta'
import Axios from 'axios'
import Container from './Components/layout/Container'

function App() {

  const cadastrar = (nome='jefferson')=>{
    Axios.post('http://localhost:3000/cadastrar',{
      descricao:nome
    }).then((response) =>{
        console.log(response)
    }).catch((err)=>{
      console.log(err)
    })
  }


  return (
    <Router>
    <Navbar/>
    <Container customClass = "min_height">

        <Routes>
          
            <Route exact path='/' element={<Home/>}>  </Route>
            <Route exact path='/Contato' element={<Contato/>}>  </Route>
            <Route exact path='/Sobre' element={<Sobre/>}>  </Route>
            <Route exact path='/Despesas' element={<Despesas/>}>  </Route>
            <Route exact path='/Nova_conta' element={<Nova_conta/>} cadstrar={cadstrar}>  </Route>
        </Routes>

    </Container>
    <Footer/>
    </Router>
  );
}

export default App;
