import {useEffect, useState} from 'react'
import Inputs from "../form/Inputs"
import Axios from "axios"
import Select from "../form/Select"
import SubmitButton from "../form/SubmitButton"

function ProjetoForm({btntext,cadastrar}){

    const [categoria,setCategoria] = useState([])

useEffect(()=>{
    
        Axios.get('http://localhost:3500/categoria').then((response) =>{
            setCategoria(response.data)
        })
        
  },[]);
  const handleOnChange =()=>{
    

  }
    

    
    return(
        <form>
            
            <Inputs type='text' text='Decrição:' name='descricao' placeholder='despesas de mercado' change={handleOnChange}/>
           {/* <div>
                <label>Descrição:</label>
                <input type="text" placeholder='exemplo: despesas de mercado' />
            </div>*/}
            <Inputs type='date' name='data' text='Data:'/>

            <Inputs type='date' name='data_vencimento' text='Vencimento:'/>

            <Inputs type='number' name='valor' text='Valor:' placeholder='R$179,55'/>
            
            <Select name='categoria_id' text='Avista / Parcelado' options={categoria}/>
                        
            <SubmitButton text={btntext} type='submit' func={cadastrar} />
           
        </form>
    )
}

export default ProjetoForm
