import { Link } from 'react-router-dom'
import styles from './Input.module.css'
function Inputs({type, text, name, placeholder, handleOnChange, value,estilo, render}){
    return(
        <div className={`${styles.form_controle} ${styles[estilo]}`}>
            <label htmlFor={name}>{text}</label>
            <input type={type} name={name} id={name} placeholder={placeholder} onChange={handleOnChange} value={value}/>
            {render === true &&
                <p className={styles.recPassword}><Link to='/Sobre'>esqueceu sua senha</Link></p>
            }
        </div>

    )
}

export default Inputs
