import {useState, useEffect} from 'react'
import Inputs from "../form/Inputs"
import Axios from "axios"

import Select from "../form/Select"
import SubmitButton from "../form/SubmitButton"
function ProjetoForm({btntext}){

    const [categoria,setCategoria] = useState({'id':'','name':''})

useEffect(() =>{
    Axios.get('http://localhost:3500/categoria').then((response) =>{
        setCategoria(response.data)})
    
},[]);
   

    return(
        <form>
            <h1>oi {categoria}</h1>
            <Inputs type='text' text='Decrição:' name='descricao' placeholder='despesas de mercado'/>
           {/* <div>
                <label>Descrição:</label>
                <input type="text" placeholder='exemplo: despesas de mercado' />
            </div>*/}
            <Inputs type='date' text='Data:'/>

            <Inputs type='date' text='Vencimento:'/>

            <Inputs type='number' text='Valor:' placeholder='R$179,55'/>
            
            <Select name='categoria_id' text='Avista / Parcelado'/>
                        
            <SubmitButton text={btntext} type='submit'/>
            

        </form>
    )
}

export default ProjetoForm
