import {useEffect, useState} from 'react'
import Axios from 'axios'
import styles from './Total.module.css'

function Total(){
    const [total,setTotal] =  useState()
    

    useEffect(()=>{
    
        Axios.get('http://localhost:3500/dividas/total').then((response) =>{
            setTotal(response.data)
        })
        
  },[]); 

 
    return(
        <div className={styles.display_lateral} >
            <h1>total despesas</h1>
            <p>R$ { total && total.map((list) =>{
                return <tr key={list.id}>
                        <td className={styles.colun_espace}>{list.descricao}</td>
                        <td className={styles.colun_center}>{list.data}</td>
                        <td className={styles.colun_center}>{list.valor}</td>                     
                </tr>
            })}}</p>
        </div>
       

    )
}

export default Total


