import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom'
import {useState} from 'react'
import Axios from 'axios'
import Home from './Components/pages/Home'
import Contato from './Components/pages/Contato'
import Sobre from './Components/pages/Sobre'
import Navbar from './Components/layout/NavBar'
import Footer from './Components/layout/Footer'
import Despesas from './Components/pages/Despesas'
import Nova_conta from './Components/pages/Nova_conta'

import Container from './Components/layout/Container'

function App() {
 
  const [dividas,setDividas] =  useState({'id':'','descricao':'','valor':'','data':'','data_vencimento':'','categoria:':'','qtd_parcelas':''});


  const cadastrar = (e)=>{
    e.preventDefault();
     //console.log('funcionou',nome)
     Axios.post('http://192.168.0.110:3500/cadastrar',{
       descricao:dividas.descricao,
       valor:dividas.valor,
       data:dividas.data,
       data_vencimento:dividas.data_vencimento,
       categoria:dividas.categoria,
       qtd_parcelas:null,

     }).then((response) =>{
         console.log(response)
     }).catch((err)=>{
       console.log(err)
     })
   }


  return (
    <Router>
      <div className={styles.navb}>
            <Navbar/>
      </div>
    <Container customClass = "min_height">

        <Routes>
          
            <Route exact path='/' element={<Home/>}>  </Route>
            <Route exact path='/Contato' element={<Contato/>}>  </Route>
            <Route exact path='/Sobre' element={<Sobre/>}>  </Route>
            <Route exact path='/Despesas' element={<Despesas dividas={dividas} setDividas={setDividas} />}>  </Route>
            <Route exact path='/Nova_conta' element={<Nova_conta cadastrar={cadastrar} dividas={dividas} setDividas={setDividas}/>}>  </Route>
        </Routes>

    </Container>
    <Footer/>
    </Router>
  );
}

export default App;
