import {useEffect, useState} from 'react'
import Inputs from "../form/Inputs"
import Axios from "axios"
import Select from "../form/Select"
import SubmitButton from "../form/SubmitButton"

function ProjetoForm({btntext,cadastrar,dividas,setDividas}){

    const [categoria,setCategoria] = useState([])
   
useEffect(()=>{
    
        Axios.get('http://localhost:3500/categoria').then((response) =>{
            setCategoria(response.data)
        })
        
  },[]);

  const handlFormChange =(e)=>{
    if(e.target.getAttribute('name') === 'descricao'){

        setDividas({'id':dividas.id,'descricao':e.target.value,'valor':dividas.valor,'data':dividas.data,'data_vencimento':dividas.data_vencimento,'categoria':dividas.categoria,'qtd_parcelas':dividas.qtd_parcelas})
    }if(e.target.getAttribute('data') === 'data'){
        console.log(e);
        console.log(e);
        /*setDividas({'id':dividas.id,'descricao':dividas.descricao,'valor':dividas.valor,'data':'','data_vencimento':dividas.data_vencimento,'categoria':dividas.categoria,'qtd_parcelas':dividas.qtd_parcelas})*/
    }
  } 

    
    return(
        <form>
            
            <Inputs type='text' text='Decrição:' name='descricao' placeholder='despesas de mercado' handleOnChange={handlFormChange}/>
            
           {/* <div>
                <label>Descrição:</label>
                <input type="text" placeholder='exemplo: despesas de mercado' />
            </div>*/}
            <Inputs type='date' name='data' text='Data:'/>

            <Inputs type='date' name='data_vencimento' text='Vencimento:'/>
            <h1>{dividas.data}</h1>

            <Inputs type='number' name='valor' text='Valor:' placeholder='R$179,55'/>
            
            <Select name='categoria_id' text='Avista / Parcelado' options={categoria}/>
                        
            <SubmitButton text={btntext} type='submit' func={cadastrar} />
           
        </form>
    )
}

export default ProjetoForm
